import React, {useState} from "react";
import Collapse from "react-bootstrap/Collapse";
import InputGroup from "react-bootstrap/InputGroup";
import Form from "react-bootstrap/Form";
import TitleWithToggle from "./TitleWithToggle";
import InputComp from "./InputComp";




function EditorComp() {

    const [openEditor, setOpenEditor] = useState(true);

    return (
        <>  
        <div className="container w-75 h-75 shadow-lg mb-auto border border-success fs-1 text-center">
                <TitleWithToggle 
                    title={"Text Editor"}
                    open={openEditor}
                    onClick={() => setOpenEditor(!openEditor)}
                />
                <Collapse in={openEditor}>
                    <div id="editor-text" className="ms-2 p-0 bg-primary">
                    <InputComp />

                    </div>
                </Collapse>
        </div>
        </>
    )
}
export default EditorComp;
