import React, {useState} from "react";

import HomeComp from "./HomeComp";
import QuoteComp from "./quoteComp";

import {Routes, Route, Link, Navigate, withRouter} from 'react-router-dom';

function MainComp() {
    const [BgColor, setBgColor] = useState(["black","green", "purple", "blue", "magenta", "red", "cyan"]);

    function Index(length) {
        return Math.floor(Math.random() * length)
    }

    function handleClick() {
        const randomIndex = Index(BgColor.length);
        const newBgColor = '#'+(0x1000000+(Math.random())*0xffffff).toString(16).substr(1,6);
        setBgColor(newBgColor);
    }

    return(
        <React.Fragment>
            <HomeComp  BgColor={BgColor} />
            <QuoteComp BgColor={BgColor} onClick={handleClick} />
        </React.Fragment>
    )
}
export default MainComp;
