import React, {useState} from "react";
// import {Link} from 'react-router-dom'
import Acordion from "react-bootstrap/Accordion";
import { useAccordionButton } from "react-bootstrap/esm/AccordionButton";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
//import { faQuoteLeft } from "@fortawesome/free-solid-svg-icons";
import { marked } from "marked";
import AccordionBody from "react-bootstrap/esm/AccordionBody";
import Card from "react-bootstrap/Card"

function ContentAwareToggle({ children, eventKey, callback}) {
    const {activeEventKey} = useContext(AccordionContext);
    const decoratedOnClick =useAccordionButton(
        eventKey,
        callback => callback(eventKey),
    );
    const isCurrentEventKey = activeEventKey === eventKey;

    return (
        <button 
            type="button"
            style={{backgroundColor: isCurrentEventKey ? "pink" : "lavender"}}
            onClick={decoratedOnClick}
        >
            {children}
        </button>
    )
}



function InputComp() {

    const html = marked.parse('# Marked in Node.js\n\nRendered by **marked**.');

    return (
        <>       
        <div className="container d-flex fs-1 justify-content-center">
            <h1>Editor </h1>
            <div>{html}</div>
            <Accordion defaultActiveKey="0">
             <Card>
                 <Card.Header>
                     <CustomToggle eventKey="0">Click me!</CustomToggle>
                 </Card.Header>
                <Accordion.Collapse eventKey="0">
                    <Card.Body>Hello! I'm the body</Card.Body>
                </Accordion.Collapse>
            </Card>
            <Card>
                <Card.Header>
                 <CustomToggle eventKey="1">Click me!</CustomToggle>
                </Card.Header>
                <Accordion.Collapse eventKey="1">
                 <Card.Body>Hello! I'm another body</Card.Body>
                </Accordion.Collapse>
           </Card>
         </Accordion>



        </div>
        </>
    )
}
export default InputComp;
