import React, {useState} from "react";
import Collapse from "react-bootstrap/Collapse";
import Form from "react-bootstrap/Form";
import ToggleButtonComp from "./ToggleButtonComp";




function EditorComp() {

    const [openEditor, setOpenEditor] = useState(true);
    const htmlText = '# Marked in Node.js\n\nRendered by **marked**.';

    return (
        <>  
        <div className="container w-50 mb-auto border border-success fs-1 text-center">
            <div className="row border shadow-lg border-success bg-primary">
                <div className="col-1"></div>
                <p className="col-10">Editor</p>
                <div className="col-1">
                    <ToggleButtonComp
                        open={openEditor}
                        onClick={() => setOpenEditor(!openEditor)}
                    />
                </div>
            </div>            
                <Collapse in={openEditor}>
                    <div id="editor-text">
                    <Form>
                        <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
                        <Form.Label></Form.Label>
                        <Form.Control as="textarea" rows={10} />
                        </Form.Group>
                    </Form>

                    </div>
                </Collapse>
        </div>
        </>
    )
}
export default EditorComp;
