import React, {useState} from "react";
import Collapse from "react-bootstrap/Collapse";
import InputGroup from "react-bootstrap/InputGroup";
import Form from "react-bootstrap/Form";
import TitleWithToggle from "./TitleWithToggle";




function EditorComp() {

    const [openEditor, setOpenEditor] = useState(true);

    return (
        <>  
        <div className="container w-75 shadow-lg mb-auto border border-success fs-1 text-center">
                <TitleWithToggle 
                    title={"Text Editor"}
                    open={openEditor}
                    onClick={() => setOpenEditor(!openEditor)}
                />
                <Collapse in={openEditor}>
                    <div id="editor-text" className="m-auto p-4">
                    <InputGroup>
                        <InputGroup.Text></InputGroup.Text>
                        <Form.Control as="textarea" aria-label="With Text"></Form.Control>
                    </InputGroup>

                    </div>
                </Collapse>
        </div>
        </>
    )
}
export default EditorComp;
