import React, {useState} from "react";
// import {Link} from 'react-router-dom'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUpRightAndDownLeftFromCenter, faDownLeftAndUpRightToCenter } from "@fortawesome/free-solid-svg-icons";
import { marked } from "marked";
import Button from "react-bootstrap/Button";
import Collapse from "react-bootstrap/Collapse";
import Form from "react-bootstrap/Form";
import MarkdownComp from "./MarkdownComp";




function InputComp() {

    const [openEditor, setOpenEditor] = useState(true);
    const [openPreview, setOpenPreview] = useState(true);

    const htmlText = marked.parse('# Marked in Node.js\n\nRendered by **marked**.');



    return (
        <>  



        <div className="container fs-1 text-align-center justify-content-center">
            <h1>Editor </h1>
            <Button
                onClick={() => setOpenEditor(!openEditor)}
                aria-controls="editor-text"
                aria-expanded={openEditor}
            >
               {(!openEditor && <FontAwesomeIcon icon={faUpRightAndDownLeftFromCenter} /> )
               || (openEditor && <FontAwesomeIcon icon={faDownLeftAndUpRightToCenter} />)}
            </Button>
            <Collapse in={openEditor}>
                <div id="editor-text">
             <Form>
                <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
                 <Form.Label></Form.Label>
                 <Form.Control as="textarea" rows={10} />
                </Form.Group>
             </Form>
                    <p>
                        <h2>This is marked down </h2><hr></hr>
                        <div dangerouslySetInnerHTML={ {__html: htmlText}}></div>
                    </p>
                </div>
            </Collapse>
            <Button
                onClick={() => setOpenPreview(!openPreview)}
                aria-controls="editor-text"
                aria-expanded={openPreview}
            >
               {(!openPreview && <FontAwesomeIcon icon={faUpRightAndDownLeftFromCenter} /> )
               || (openPreview && <FontAwesomeIcon icon={faDownLeftAndUpRightToCenter} />)}
            </Button>
            <Collapse in={openPreview}>
                <div id="editor-text">
             <Form>
                <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
                 <Form.Label></Form.Label>
                 <Form.Control as="textarea" rows={10} />
                </Form.Group>
             </Form>
                    <p>
                        <h2>This is NOT marked down </h2><hr></hr>
                        {htmlText}
                    </p>
                </div>
            </Collapse>
        </div>
        </>
    )
}
export default InputComp;
