import React, {useState} from "react";
// import {Link} from 'react-router-dom'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUpRightAndDownLeftFromCenter, faDownLeftAndUpRightToCenter } from "@fortawesome/free-solid-svg-icons";
import { marked } from "marked";
import Button from "react-bootstrap/Button";
import Collapse from "react-bootstrap/Collapse";
import Form from "react-bootstrap/Form";




function EditorComp() {

    const [openEditor, setOpenEditor] = useState(true);

    const htmlText = marked.parse('# Marked in Node.js\n\nRendered by **marked**.');



    return (
        <>  
        <div className="container m-3 fs-1 text-center justify-content-center">
            <h1>Editor </h1>
                <Button
                    onClick={() => setOpenEditor(!openEditor)}
                    aria-controls="editor-text"
                    aria-expanded={openEditor}
                >
                {(!openEditor && <FontAwesomeIcon icon={faUpRightAndDownLeftFromCenter} /> )
                || (openEditor && <FontAwesomeIcon icon={faDownLeftAndUpRightToCenter} />)}
                </Button>
                <Collapse in={openEditor}>
                    <div id="editor-text">
                    <Form>
                        <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
                        <Form.Label></Form.Label>
                        <Form.Control as="textarea" rows={10} />
                        </Form.Group>
                    </Form>
                    <p>
                        <h2>This is NOT marked down </h2>
                        {htmlText}
                    </p>
                    </div>
                </Collapse>
        </div>
        </>
    )
}
export default EditorComp;
