import React, {useState} from "react";
// import {Link} from 'react-router-dom';
import Alert from "react-bootstrap/Alert";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUpRightAndDownLeftFromCenter, faDownLeftAndUpRightToCenter } from "@fortawesome/free-solid-svg-icons";
import { marked } from "marked";
import Button from "react-bootstrap/Button";
import Collapse from "react-bootstrap/Collapse";
import Form from "react-bootstrap/Form";


function MarkdownComp() {

    const [openPreview, setOpenPreview] = useState(true);
    const htmlText = marked.parse('# Marked in Node.js\n\nRendered by **marked**.');

    return (
        <>
        <div className="container border border-success-subtle mt-auto fs-1 text-center">
           <div className="row border">
                <h2 className="col-11 me-auto">Markdown Preview</h2>
                <Button
                    className="col-1"
                    onClick={() => setOpenPreview(!openPreview)}
                    aria-controls="editor-text"
                    aria-expanded={openPreview}
                >
                   {(!openPreview && <FontAwesomeIcon icon={faUpRightAndDownLeftFromCenter} /> )
                   || (openPreview && <FontAwesomeIcon icon={faDownLeftAndUpRightToCenter} />)}
                </Button>
            </div> 
            <Collapse in={openPreview}>
                <div id="editor-text">
                    <p>
                        <h2>This is marked down </h2>
                        <div dangerouslySetInnerHTML={ {__html: htmlText}}></div>
                    </p>
                </div>
            </Collapse>
        </div>  
        </>
    )
}
export default MarkdownComp;
